mapper.select:
SELECT P.*, F.address_sido, F.address_sigungu, F.farm_name FROM marifarm.carbon_products P JOIN marifarm.projects PJ ON P.project_id = PJ.project_id JOIN marifarm.farms F ON PJ.farm_id = F.farm_id WHERE P.project_id IN
#{id}
ORDER BY P.cp_id DESC

SELECT token_id 
FROM marifarm.carbon_products 
WHERE project_id = #{projectId}

SELECT cp_id AS cpId, project_id AS projectId, cp_title AS title, cp_type AS category, cp_price AS cpPrice, cp_amount AS cpAmount, product_certificate AS productCertificate, vintage_year AS vintageYear, cp_detail AS cpDetail, cp_title FROM carbon_products
AND cp_type = #{category}
ORDER BY cp_id DESC
