SELECT P.*, F.address_sido, F.address_sigungu, F.farm_name FROM carbon_products P JOIN projects PJ ON P.project_id = PJ.project_id JOIN farms F ON PJ.farm_id = F.farm_id WHERE P.project_id IN
#{id}
ORDER BY P.cp_id DESC

SELECT token_id 
FROM tokens
WHERE project_id = #{projectId}

SELECT actual_amount 
FROM projects 
WHERE project_id = #{projectId}

SELECT cp.cp_id AS cpId, cp.project_id AS projectId, cp.cp_title AS cpTitle, cp.cp_type AS category, cp.cp_price AS cpPrice, cp.cp_amount AS cpAmount, cp.product_certificate AS productCertificate, cp.vintage_year AS vintageYear, cp.cp_detail AS cpDetail, pi.image_url AS thumbnailUrl FROM carbon_products cp LEFT JOIN projects p ON cp.project_id = p.project_id LEFT JOIN project_images pi ON pi.project_id = p.project_id AND pi.is_thumbnail = true
AND cp.cp_type = #{category}
ORDER BY cp.cp_id DESC

SELECT cp_title
FROM carbon_products
WHERE cp_id = #{cpId}

SELECT cp_amount
FROM carbon_products
WHERE cp_id = #{cpId}
