mapper.select:
SELECT ucl_id 
FROM user_certificate_links 
WHERE user_id = #{userId}

SELECT p.project_id AS projectId, p.project_name AS projectName, p.project_status AS projectStatus, sh.subscription_status AS subscriptionStatus, p.project_start_date AS projectStartDate, p.project_end_date AS projectEndDate FROM projects p JOIN subscription_hists sh ON sh.project_id = p.project_id WHERE sh.user_id = #{userId}
AND p.project_status = #{status}
ORDER BY p.project_id DESC LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*)
FROM projects p
JOIN subscription_hists sh
  ON sh.project_id = p.project_id
WHERE sh.user_id = #{userId}

    AND p.project_status = #{status}

SELECT p.project_id AS projectId, p.project_name AS projectName, p.project_status AS projectStatus, NULL AS subscriptionStatus, p.project_start_date AS projectStartDate, p.project_end_date AS projectEndDate FROM projects p JOIN starred_projects sp ON sp.project_id = p.project_id WHERE sp.user_id = #{userId} AND sp.is_starred = TRUE
AND p.project_status = #{status}
ORDER BY p.project_id DESC LIMIT #{limit} OFFSET #{offset}
